					function frecuenciaMunicipios($municipio,$condicional){
						include("../script/conexion.php");
						$sql1 = "SELECT * FROM encuesta1 WHERE municipio = '".$municipio."' GROUP BY ".$condicional;
						$proceso1 = mysqli_query($conexion,$sql1);
						$contador1 = mysqli_num_rows($proceso1);
						$total = 0;
						$i = 1;

			    		while($row1 = mysqli_fetch_array($proceso1)) {
			    			$opciones_array[$i] = $row1[$condicional];
			    			$i = $i+1;
		    			}

		    			for($j=1;$j<=$contador1;$j++){
		    				$sql2 = "SELECT COUNT(*) AS total FROM encuesta1 WHERE municipio = '".$municipio."' and ".$condicional." = ".$opciones_array[$j];
							$proceso2 = mysqli_query($conexion,$sql2);
				    		while($row2 = mysqli_fetch_array($proceso2)) {
				    			$array_final[$municipio][$condicional]["datos"][$opciones_array[$j]] = $row2["total"];
				    			$total = $total + $row2["total"];
			    			}
		    			}
		    			$array_final[$municipio][$condicional]["datos"]["total"] = $total;

		    			for($g=1;$g<=$contador1;$g++){
		    				if($array_final[$municipio][$condicional]["datos"][$opciones_array[$g]]==0){
		    					$array_final[$municipio][$condicional]["porcentajes"][$g] = "0%";
		    				}else{
		    				$array_final[$municipio][$condicional]["porcentajes"][$g] = number_format(($array_final[$municipio][$condicional]["datos"][$opciones_array[$g]]/$array_final[$municipio][$condicional]["datos"]["total"])*100,2, '.', ' ')."%";
		    				}
		    			}

		    			return $array_final;
					}

